$(function(){$("select").on("change",function(e){const t=$(this).val();if($(".articles").empty(),t){$(".container").addClass("loaded");const e=`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=82f12f3e4189492f86578cb2d4e6add0`;$.ajax({url:e,method:"GET"}).done(function(e){let t=0,n=0;for(;t<e.results.length&&n<12;){t++;let a=e.results[t];if(a.multimedia.length){n++;let e=a.abstract,t=a.url,i=a.multimedia[4].url;$(".articles").append(`\n                          <div>\n                              <a href="${t}">\n                                  <img src="${i}">\n                                  <p>${e}</p>\n                              </a>\n                          </div>`)}}}).fail(function(e){throw e})}else $(".container").removeClass("loaded")})});